import UIKit

@objc protocol IntroRoutingLogic {
    func showPokemonDetail()
}

protocol IntroDataPassing {
    var dataStore: PokemonListDataStore? { get }
}

class PokemonListRouter: NSObject, IntroRoutingLogic, IntroDataPassing {
    weak var viewController: PokemonListViewController?
    var dataStore: PokemonListDataStore?

    // MARK: Routing

    func showPokemonDetail() {

        let destinationVC = PokemonDetailViewController()
        var destinationDS = destinationVC.router!.dataStore!
        passDataToSomewhere(source: dataStore!, destination: &destinationDS)
        navigateToSomewhere(source: viewController!, destination: destinationVC)
    }

    // MARK: Navigation

    func navigateToSomewhere(source: PokemonListViewController, destination: PokemonDetailViewController) {
        source.navigationController?.pushViewController(destination, animated: true)
    }

    // MARK: Passing data
    func passDataToSomewhere(source: PokemonListDataStore, destination: inout PokemonDetailDataStore) {
        destination.selectedPokemon = source.selectedPokemon
    }
}
